// $Id: admin.menu.js,v 1.1.2.9 2010/08/01 05:07:31 yhahn Exp $

Drupal.behaviors.adminToolbarMenu = function(context) {
  if (jQuery().drilldown) {
    $('#admin-toolbar div.admin-block:has(ul.menu):not(.admin-toolbar-menu)')
      .addClass('admin-toolbar-menu')
      .each(function() {
        var menu = $(this);
        var trail = '#admin-toolbar div.admin-tab.' + $(this).attr('id').split('block-')[1] + ' span';
        var rootTitle = $(trail).text();

        if ($('a:has(span.menu-description)', menu).size() > 0) {
          menu.addClass('admin-toolbar-menu-hover');
          $('a:has(span.menu-description)', menu).hover(
            function() {
              $('<a></a>')
                .attr('class', $(this).attr('class'))
                .addClass('menu-hover')
                .append($('span.menu-description', this).clone())
                .appendTo(menu)
                .show();
            },
            function() {
              $(menu)
                .children('a.menu-hover')
                .remove();
            }
          );
        }

        // Replace the standard trail click handler with one that only
        // adjusts menu if the admin tab is active. Otherwise, switch
        // to that admin tab.
        menu.bind('refresh.drilldown', function() {
          $(trail + ' a').unbind('click').click(function() {
            if ($(this).parents('div.admin-tab').is('.admin-tab-active')) {
              var settings = {'activeLink': $(this).data('original'), 'trail': trail};

              // If the clicked link does not reference the current
              // active menu, set it to be active.
              if (settings.activeLink.siblings('ul.drilldown-active-menu').size() === 0) {
                menu.drilldown('setActive', settings);
                return false;
              }
              // Otherwise, pass-through and allow the link to be clicked.
              return menu.drilldown('goTo', settings);
            }
            $(this).parents('div.admin-tab').click();
            return false;
          });
        });

        // Init drilldown plugin.
        menu.drilldown('init', {
          activePath: Drupal.settings.activePath,
          trail: trail,
          rootTitle: rootTitle
        });
      });
  }
};
;
Drupal.behaviors.blvdSearch = function(context) {
	jQuery('form.blvd-search-apartments').each(function(){
		new Drupal.blvdSearch(this, Drupal.settings.blvdSearch.apartments);
	});
	jQuery('form.blvd-search-homes').each(function(){
		new Drupal.blvdSearch(this, Drupal.settings.blvdSearch.homes);
	});
}

Drupal.blvdSearch = function(form, settings)
{
	jQuery(form).submit(function() {
		filters = new Array();
		keyword = jQuery('input[name=keywords]', form).val().replace(settings.keywordDefault, '');
		bed = jQuery('select[name=bedrooms]', form).val();
		bath = jQuery('select[name=bathrooms]', form).val();
		town = jQuery('select[name=town]', form).val();
		pets = jQuery('#edit-pets', form).val();
		parking = jQuery('#edit-parking', form).val();
		views = jQuery('select[name=views]', form).val();
		pricemin = jQuery('.price select[name=min]', form).val();
		pricemax = jQuery('.price select[name=max]', form).val();
		
		if(pricemax > 0)
			pricemin = pricemin > 0 ? parseInt(pricemin) : 0;
		else if(pricemin > 0)
			pricemax = '*';
		
		if(bed) filters.push('tid:' + bed);
		if(bath) filters.push('tid:' + bath);
		if(town) filters.push('tid:' + town);
		if(pets) filters.push('tid:' + pets);
		if(parking) filters.push('tid:' + parking);
		if(views) filters.push('tid:' + views);
		if(pricemin || pricemax) filters.push('ps_cck_field_rent:[' + pricemin + ' TO ' + pricemax + ']');
		
		window.location = form.action + (keyword ? '/' + keyword : '') + (filters.length ? '?filters=' + filters.join(' ') : '');
		return false;
	});
	
	// onfocus, onblur reset hint text.
	jQuery('input[name=keywords]', form).each(function(){
		jQuery(this).focus(function(){
			if(jQuery(this).val() == settings.keywordDefault) jQuery(this).val('');
		});
		jQuery(this).blur(function(){
			if(jQuery(this).val() == '') jQuery(this).val(settings.keywordDefault);
		});
		
	});
	
	// bldg jumplist
	jQuery('select[name=bldg]', form).each(function(){
		jQuery(this).change(function(){
			if(jQuery(this).val() != '')
				window.location = jQuery(this).val();
		});
	});
};
